var myModule=angular.module("sendRequest",[]);myModule.controller('getInfoFromServer', ['$scope','$http', function($scope,$http){    $http({        method: 'GET',        url: 'js/data.json'    }).success(function(data, status, headers, config) {        console.log(data);        $scope.info= data;    }).error(function(data, status, headers, config) {        console.log("false");    });    $scope.sendMsg= function(){        $http({            method: 'post',            url: '/index'        }).success(function(data, status, headers, config) {            console.log(data);            $scope.info= data;        }).error(function(data, status, headers, config) {            console.log("false");        });    }}]);